package thisKeyWord;

public class SwitchMonths {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String monthString="May";
		
		switch (monthString) {
		case "January":
			System.out.println("the month is "+monthString);
			break;
		case "Feburary":
			System.out.println("the nonth is "+monthString);
			break;
		case "March":
			System.out.println("the nonth is "+monthString);
			break;
		case "April":
			System.out.println("the nonth is "+monthString);
			break;
		case "May":
			System.out.println("the nonth is "+monthString);
			break;
		default:
			break;
		}

	}

}
